mapper.insert:


  SELECT LAST_INSERT_ID()

insert into workflowInstance (workflowId, sender, status, 
  createTime, updateTime, attachmentId, 
  activeNode, canbeEdit, summary
  )
values (#{workflowid,jdbcType=INTEGER}, #{sender,jdbcType=INTEGER}, #{status,jdbcType=INTEGER}, 
  #{createtime,jdbcType=TIMESTAMP}, #{updatetime,jdbcType=TIMESTAMP}, #{attachmentid,jdbcType=INTEGER}, 
  #{activenode,jdbcType=INTEGER}, #{canbeedit,jdbcType=INTEGER}, #{summary,jdbcType=VARCHAR}
  )

mapper.select:
select wf.name as workflowName, wfi.*, wft.`name` as workflowType, wfs.`name` as workflowStatus, u.userName as senderName, wfn.nodeName, d.`name` as departmentName, u2.userName as receiverName from workflowInstance as wfi left join workflow as wf on wfi.workflowId = wf.id left join workflowType as wft on wf.type = wft.id left join workflowStatus as wfs on wfi.status = wfs.id left join `user` as u on wfi.sender = u.id left join workflowNode as wfn on wfi.activeNode = wfn.id left join `user` as u2 on wfn.receiver = u2.id left join deparment as d on u.departmentId = d.id
where wfi.sender = #{userId} and wfi.status != 5 and wfi.status != 6
order by wfi.updateTime desc limit #{start}, #{limit}

select wf.name as workflowName, wfi.*, wft.`name` as workflowType, wfs.`name` as workflowStatus, u.userName as senderName, wfn.nodeName, d.`name` as departmentName, u2.userName as receiverName from workflowInstance as wfi left join workflow as wf on wfi.workflowId = wf.id left join workflowType as wft on wf.type = wft.id left join workflowStatus as wfs on wfi.status = wfs.id left join `user` as u on wfi.sender = u.id left join workflowNode as wfn on wfi.activeNode = wfn.id left join `user` as u2 on wfn.receiver = u2.id left join deparment as d on u.departmentId = d.id
where wfn.receiver = #{userId}
order by wfi.updateTime desc limit #{start}, #{limit}

select wf.name as workflowName, wfi.*, wft.`name` as workflowType, wfs.`name` as workflowStatus, u.userName as senderName, wfn.nodeName, d.`name` as departmentName, u2.userName as receiverName from workflowInstance as wfi left join workflow as wf on wfi.workflowId = wf.id left join workflowType as wft on wf.type = wft.id left join workflowStatus as wfs on wfi.status = wfs.id left join `user` as u on wfi.sender = u.id left join workflowNode as wfn on wfi.activeNode = wfn.id left join `user` as u2 on wfn.receiver = u2.id left join deparment as d on u.departmentId = d.id
where wfi.sender = #{userId} and wfi.status = 5
order by wfi.updateTime desc limit #{start}, #{limit}

select wf.name as workflowName, wfi.*, wft.`name` as workflowType, wfs.`name` as workflowStatus, u.userName as senderName, wfn.nodeName, d.`name` as departmentName, u2.userName as receiverName from workflowInstance as wfi left join workflow as wf on wfi.workflowId = wf.id left join workflowType as wft on wf.type = wft.id left join workflowStatus as wfs on wfi.status = wfs.id left join `user` as u on wfi.sender = u.id left join workflowNode as wfn on wfi.activeNode = wfn.id left join `user` as u2 on wfn.receiver = u2.id left join deparment as d on u.departmentId = d.id
where wfi.sender = #{userId} and wfi.status = 6
order by wfi.updateTime desc limit #{start}, #{limit}

select distinct wfi.createTime, wf.name as workflowName, wfi.*, wft.`name` as workflowType, wfs.`name` as workflowStatus, u.userName as senderName, wfn.nodeName, d.`name` as departmentName, u2.userName as receiverName from workflowInstance as wfi left join workflow as wf on wfi.workflowId = wf.id left join workflowType as wft on wf.type = wft.id left join workflowStatus as wfs on wfi.status = wfs.id left join `user` as u on wfi.sender = u.id left join workflowNode as wfn on wfi.activeNode = wfn.id left join `user` as u2 on wfn.receiver = u2.id left join deparment as d on u.departmentId = d.id left join workflowReceiver4cc as cc on wfi.workflowId = cc.workflowId and cc.receiver = #{userId}
where wfi.status != 6
order by wfi.updateTime desc limit #{start}, #{limit}
